mapper.select:
select board_id, board_date, board_img_path, board_content, board_hit, board_like 
  from board 
where board_id=#{value}

mapper.update:
update board
	 set board_hit = #{board_hit}
 where board_id = #{board_id}

update board
	 set board_like = #{board_like}
 where board_id = #{board_id}

mapper.select:
select comment_id, comments, comment_date
  from comments
where board_id=#{board_id}

mapper.update:
update comments
     set comments=#{comments}
 where board_id=#{board.board_id}
    and id = #{member.id}
    and comment_id = #{comment_id}

mapper.delete:
delete from comments
 where board_id=#{board.board_id}
    and id = #{member.id}
    and comment_id = #{comment_id}
